definition.script:
pipeline {
   agent {label 'agent1'}

    stages {
        stage('CHECKOUT') {
            steps {
               echo "Getting Project from Git"; 
                git branch: "main", 
                    url: "https://github.com/raniabns/ski-devops.git"
                    //credentialsId: "git-jenkins-credentials"; 
            }
        }
         stage('Build Artifact') {
            steps {
                sh 'mvn package -DskipTests'


            }
        }
         stage('Build Docker image') {
            steps {
                sh 'docker build -app-container .'


            }
        }

        stage('SonarCloud analysis') {
            steps {
                // Analyse du code avec SonarCloud

                        sh 'mvn verify sonar:sonar -Dsonar.organization=ski-station-devops -Dsonar.host.url=https://sonarcloud.io -Dsonar.token=16aadcafad9e921cf0c36411e78a98c02a03777a'
                }

            }
    }
    }
